SET client_encoding = 'UTF8';

SET standard_conforming_strings = on;

DROP TABLE IF EXISTS "受注明細";
DROP TABLE IF EXISTS "受注";
DROP TABLE IF EXISTS "得意先";
DROP TABLE IF EXISTS "商品";

CREATE TABLE IF NOT EXISTS "商品" (
    "商品コード" VARCHAR(20) PRIMARY KEY,
    "商品名" VARCHAR(100) NOT NULL,
    "単価" NUMERIC(12, 0) NOT NULL,
    "備考" VARCHAR(200),
    "Version" INTEGER NOT NULL DEFAULT 1
);

CREATE TABLE IF NOT EXISTS "得意先" (
    "得意先ID" INTEGER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
    "得意先名" VARCHAR(100) NOT NULL,
    "電話番号" VARCHAR(20),
    "備考" VARCHAR(200),
    "Version" INTEGER NOT NULL DEFAULT 1
);

CREATE TABLE IF NOT EXISTS "受注" (
    "受注ID" INTEGER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
    "受注日" TIMESTAMP WITHOUT TIME ZONE NOT NULL,
    "得意先ID" INTEGER NOT NULL,
    "得意先名" VARCHAR(100) NOT NULL,
    "合計金額" NUMERIC(18, 2) NOT NULL DEFAULT 0,
    "備考" VARCHAR(200),
    "Version" INTEGER NOT NULL DEFAULT 1
);

CREATE TABLE IF NOT EXISTS "受注明細" (
    "明細ID" INTEGER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
    "受注ID" INTEGER NOT NULL,
    "商品コード" VARCHAR(20) NOT NULL,
    "商品名" VARCHAR(100) NOT NULL,
    "単価" NUMERIC(12, 2) NOT NULL,
    "数量" INTEGER NOT NULL,
    CONSTRAINT fk_受注明細_受注 FOREIGN KEY ("受注ID") REFERENCES "受注" ("受注ID") ON DELETE CASCADE
);

















































































































































































