SELECT setval(pg_get_serial_sequence('"受注"', '受注ID'), COALESCE((SELECT MAX("受注ID") FROM "受注"), 0), true);

SELECT setval(pg_get_serial_sequence('"受注明細"', '明細ID'), COALESCE((SELECT MAX("明細ID") FROM "受注明細"), 0), true);
